mapper.select:
select id, name, data_type AS dataType, date_url AS dateUrl, create_time AS createTime from b_data_source




    1 = 1

        and name LIKE CONCAT('%', #{name},'%')


        and create_time > #{beginTime}


        and create_time < #{endTime}

    and iscancel = 0

    order by create_time, id
    limit #{page.offset},#{page.pageSize}
